        if(searchedItem!=="search"){
            getImages(searchedItem,dispatchUserEvent)
            // searchItemRelatedPic(searchedItem)
        }
        else{
            getImages(searchedItemPic,dispatchUserEvent)
        }
console.log(searchedItemPic,"searchedItemPic")
